import React, { Component } from "react";
import Styles from "./css/main.module.css";
import Aux from "./Aux";
import Search from "./Search";
import Book from "./Book";
import Button from "./Button";

class Library extends Component {
    state = {
        books: [
            { name: "piccolo ano", author: "grande ano", key: 1 },
            { name: "zero zero zero", author: "savi ano", key: 2 },
            { name: "le avventure di", author: "montalb ano", key: 3 },
        ],
        query: "",
    };

    updateQuery = (query) => {
        this.setState(() => ({
            query: query.trim(),
        }));
    };
    clearQuery = () => {
        this.updateQuery("");
    };


    render() {
        const { books, query } = this.state;

        const showingBooks = query === ''
        ? books
        : books.filter((b)=>(
            b.name.toLocaleLowerCase().includes(query.toLocaleLowerCase())
        ));

        return (
            <Aux>
                <div className={Styles.Library}>
                    <Search onChange={(event)=>this.updateQuery(event.target.value)}/>

                    <h1 style={{ ttextAlign: "center" }}>Library</h1>
                    <div className={Styles.bookStore}>

                        {showingBooks.map((b) => (
                            <li key={b.key}>
                                <Book name={b.name} author={b.author} />
                            </li>
                        ))}

                    </div>
                </div>
            </Aux>
        );
    }
}

export default Library;
