import React from "react";
import Styles from "./css/main.module.css";
import Aux from "./Aux";
import { Menu, Dropdown } from "antd";

const Book = (props) => {
    const { cover, onShelfUpdate, id, book } = props;
    const menu = (
        <Menu >
            <Menu.Item key='0' value='currentlyReading'>
                Add to Current Readings
            </Menu.Item>
            <Menu.Item key='1'>
                <a
                    value='read'
                    onClick={(e) => onShelfUpdate(book, e.target.value)}>
                    Add to Read
                </a>
            </Menu.Item>
            <Menu.Item key='3'>
                <a
                    value='wantToRead'
                    onClick={(e) => onShelfUpdate(book, e.target.value)}>
                    Add to Reading List
                </a>
            </Menu.Item>
        </Menu>
    );
    return (
        <Dropdown overlay={menu} trigger={["click"]} value={book.shelf} onClick={(e) => onShelfUpdate(book, e.target.value)}>
            <div style={{ backgroundImage: cover }} className={Styles.book}>
                <a onClick={(e) => e.preventDefault()} />
            </div>
        </Dropdown>
    );
};

export default Book;
