import React from "react";
import Styles from "./css/main.module.css";
import Aux from "./Aux";
import { Menu, Dropdown } from "antd";

const Book = (props) => {
    const { cover, onShelfUpdate, id } = props;
    const menu = (
        <Menu>
            <Menu.Item key='0' value='CurrentlyReading' onClick={(e)=>onShelfUpdate()}>Add to Current Readings</Menu.Item>
            <Menu.Item key='1' value='read' onClick={(e)=>onShelfUpdate()}>Add to Read</Menu.Item>
            <Menu.Item key='3' value='wantToRead' onClick={(e)=>onShelfUpdate()}>Add to Reading List</Menu.Item>
        </Menu>
    );
    return (
        <Dropdown overlay={menu} trigger={["click"]}>
            <div style={{ backgroundImage: cover }} className={Styles.book}>
                <a onClick={(e) => e.preventDefault()} />
            </div>
        </Dropdown>
    );
};

export default Book;
