    const { cover, onShelfUpdate, id, book } = props;

    return (
        <div style={{ backgroundImage: cover }} className={Styles.book}>

            <div className={Styles.dropdownContent}>
                <li>
                    {" "}
                    <a href='#'>view</a>
                </li>
                <li value='CurrentlyReading'>
                    {" "}
                    <a onClick={e=>onShelfUpdate(book, e.target.value)}> Current Readings</a>
                </li >

                <li value='wantToRead'>
                    {" "}
                    <a href='#'>Want To Read </a>
                </li>
                <li value='read'>
                    {" "}
                    <a href='#'> Read</a>
                </li>

            </div>
        </div>
    );
};

export default Book;
