import React, { Component } from "react";
import Styles from "./css/main.module.css";
import Aux from "./Aux";
import Search from "./Search";
import Book from "./Book";
import Button from "./Button";
import * as BooksAPI from "./BooksAPI";
import { render } from "@testing-library/react";

class Library extends Component {
    state = {
        shelfChange: false,
        flag: true,
        books: [],
    };

    componentDidMount() {
        console.log("componentDidMount");
        BooksAPI.getAll().then((books) =>
            this.setState(() => ({
                books,
            }))
        );
    }

    showMessageHandler = (prevState) => {
        this.setState((prevState) => ({
            shelfChange: !prevState.shelfChange,
        }));
        setTimeout(() => {this.state.flag = !this.state.flag}, 1000);
    };

    render() {
        const { books, query, onShelfUpdate, onUpdateQuery } = this.props;
        console.log(books);
        const { flag } = this.state;

        const showingBooks =
            query === ""
                ? books
                : books.filter((b) =>
                      b.title.toLowerCase().includes(query.toLowerCase())
                  );
        const showMessage = (
            <div className={Styles.showMessage}>
                <p> the books has been succesfully moved </p>
            </div>
        );

        return (
            <Aux>
                <div className={Styles.Library}>
                    <Search onUpdateQuery={onUpdateQuery} />
                    {flag == this.state.shelfChange && showMessage}

                    {console.log(this.state.shelfChange)}

                    <h1 style={{ textAlign: "center" }}>Book Store</h1>
                    <div className={Styles.bookStore}>
                        {showingBooks.map((b) => (
                            <li key={b.key}>
                                <Book
                                    onShelfUpdate={onShelfUpdate}
                                    cover={`url(${b.imageLinks.thumbnail})`}
                                    book={b}
                                    shelfChange={this.showMessageHandler}
                                />
                            </li>
                        ))}
                    </div>
                </div>
            </Aux>
        );
    }
}

export default Library;
