import React, {Component} from  'react';
import Aux from './Aux';
import Shelf from './Shelf';

class BookShelf extends Component {

    state ={
        shelves:["wantToRead", "currentlyReading", "read"]
    }

    render(){

    const {shelves} =this.state;
        return(
            <Aux>
                <ul>
               {shelves.map((shelf)=>(
                   <li className='bookShelf'>
                       <Shelf name={shelf}/>
                   </li>

               ))}
               </ul>

            </Aux>
        )
    }
}

export default BookShelf
