import React, { Component } from "react";
import Aux from "./Aux";
import Shelf from "./Shelf";
import Styles from "./css/main.module.css";
import Book from "./Book";

class BookShelf extends Component {
    state = {
        shelves: ["Want To Read", "Currently Reading", "Read"],
        books: [
                         { name: "piccolo ano", author: "grande ano", key: 1 },
            { name: "zero zero zero", author: "savi ano", key: 2 },
            { name: "le avventure di", author: "montalb ano", key: 3 },
        ],
    };

    render() {
        const { shelves, books } = this.state;
        return (
            <Aux>
                <ul className={Styles.bookShelf}>
                    {shelves.map((shelf) => (
                        <li className='shelves'>
                            <Shelf name={shelf} books={books}></Shelf>
                        </li>
                    ))}
                </ul>
            </Aux>
        );
    }
}

export default BookShelf;
