import react from "react";
import Styles from "./css/main.module.css"


const Search = (props) =>{

    const {query} = props
    let q
    return(

       <input 

       className={Styles.searchBook}
       type='text'
       placeholder='search books'
       value={props.query}
       onChange={(event)=>q=event.target.value}
       ></input>

    )
}

export default Search;
